import {Preview} from '@alinea/dashboard'
workspace('My workspace', {
  // ... workspace options
  preview({entry}) {
    return (
      <Preview>
        <h1>{entry.title}</h1>
      </Preview>
    )
  }
})

import {Preview} from '@alinea/dashboard'
workspace('My workspace', {
  // ... workspace options
  preview({previewToken}) {
    return (
      <BrowserPreview
        url={`http://localhost:3000/api/preview?${previewToken}`}
      />
    )
  }
})
