import {Config, Field} from "alinea"

export default Config.type("Tabs field", {
  fields: {
    ...Field.tabs(
      Field.tab("Tab A", {
        fields: {
          fieldA: Field.text("Field in Tab A")
        }
      }),
      Field.tab("Tab B", {
        fields: {
          fieldB: Field.text("Field in Tab B")
        }
      })
    )
  }
})
