// These are defined in cms.tsx and 
// will be used in the examples below
const BlogPost = alinea.type(...)
const schema = {BlogPost}
const cms = createCMS({schema, ...})

// Start a transaction to create a new entry of type BlogPost
const post = cms.create(BlogPost).set({
  title: 'A new blog post',
  body: 'Hello world'
})

// The new entry ID can be read before comitting
console.log(`Creating post with id: ${post.entryId}`)

// Save the changes
await post.commit()

import {Entry} from 'alinea/core'

// Select the first blog post
const blogPost = await cms.get(BlogPost().select({entryId: Entry.entryId}))

// Edit a field and save
await cms.edit(blogPost.entryId, BlogPost).set({
  body: 'New body text'
}).commit()

const file = new File(['content'], 'test.txt')
const upload = cms.upload(file)

// The new entry ID can be read before comitting
console.log(`Creating post with id: ${upload.entryId}`)

// Upload file and save file metadata
await upload.commit()
