import {Field, ScalarField, Hint, Label} from 'alinea/core'
import {InputLabel, InputState, useInput} from 'alinea/editor'

interface RangeFieldOptions {
  min?: number
  max?: number
}

class RangeField extends ScalarField<number, RangeFieldOptions> {
}

function range(label: Label, options: RangeFieldOptions = {}): RangeField {
  return new RangeField({
    hint: Hint.Number(),
    label,
    options,
    view: RangeInput
  })
}

interface RangeInputProps {
  state: InputState<InputState.Scalar<number>>
  field: RangeField
}

function RangeInput({state, field}: RangeInputProps) {
  const [value = 5, setValue] = useInput(state)
  const {label, options} = field[Field.Data]
  const {min = 0, max = 10} = options
  return (
    <InputLabel label={label}>
      <input 
        type="range" 
        min={min} max={max} 
        value={value} 
        onChange={e => setValue(Number(e.target.value))} 
      />
    </InputLabel>
  )
}

export default alinea.type('Kitchen sink', {
  ...alinea.tabs(
