import {alinea} from 'alinea'
import {Field, Hint, Label, Shape} from 'alinea/core'
import {InputLabel, InputState, useInput} from 'alinea/editor'

export interface RangeFieldOptions {
  min?: number
  max?: number
  help?: Label
}

export interface RangeField extends Field.Scalar<number> {
  label: Label
  options?: RangeFieldOptions
}

// The constructor function is used to create fields in our schema
// later on. It is usually passed a label and options.
export function range(label: Label, options?: RangeFieldOptions): RangeField {
  return {
    shape: new ScalarShape(label),
    label,
    options,
    view: RangeInput,
    hint: Hint.Number()
  }
}

interface RangeInputProps {
  state: InputState<InputState.Scalar<number>>
  field: RangeField
}

// To view our field we can create a React component. 
// This component can call the useInput hook to receive the
// current value and a method to update it.
function RangeInput({state, field}: RangeInputProps) {
  const [value = 5, setValue] = useInput(state)
  const {min = 0, max = 10, help} = field.options || {}
  return (
    <InputLabel label={field.label} help={help}>
      <input 
        type="range" 
        min={min} max={max} 
        value={value} 
        onChange={e => setValue(Number(e.target.value))} 
      />
    </InputLabel>
  )
}

export default alinea.type('Kitchen sink',
  alinea.tabs(
    alinea.tab('Basic fields', {
      title: alinea.text('Text field'),
      path: alinea.path('Path field', {
        help: 'Creates a slug of the value of another field'
      }),
      richText: alinea.richText('Rich text field'),
      select: alinea.select('Select field', {
        a: 'Option a',
        b: 'Option b'
      }),
      number: alinea.number('Number field', {
        minValue: 0,
        maxValue: 10
      }),
      check: alinea.check('Check field', {label: 'Check me please'}),
      date: alinea.date('Date field'),
      code: alinea.code('Code field')
    }),
    alinea.tab('Link fields', {
      externalLink: alinea.url('External link'),
      entry: alinea.entry('Internal link'),
      linkMultiple: alinea.link.multiple('Mixed links, multiple'),
      image: alinea.entry('Image link'),
      file: alinea.entry('File link')
    }),
    alinea.tab('List fields', {
      list: alinea.list('My list field', {
        schema: alinea.schema({
          Text: alinea.type('Text', {
            title: alinea.text('Item title'),
            text: alinea.richText('Item body text')
          }),
          Image: alinea.type('Image', {
            image: alinea.image('Image')
          })
        })
      })  
    }),
    alinea.tab('Inline fields', {
      street: alinea.text('Street', {width: 0.6, inline: true, multiline: true}),
      number: alinea.text('Number', {width: 0.2, inline: true}),
      box: alinea.text('Box', {width: 0.2, inline: true}),
      zip: alinea.text('Zipcode', {width: 0.2, inline: true}),
      city: alinea.text('City', {width: 0.4, inline: true}),
      country: alinea.text('Country', {
        width: 0.4,
        inline: true
      })
    }),
    alinea.tab('Custom fields', {
      range: range('Range field', {
        help: 'See the custom field guide'
      })  
    })
  )
)
