body.code:
import {Preview} from 'alinea'
workspace('My workspace', {
  // ... workspace options
  preview({entry}) {
    return (
      <Preview>
        <h1>{entry.title}</h1>
      </Preview>
    )
  }
})

import {BrowserPreview} from 'alinea'
workspace('My workspace', {
  // ... workspace options
  preview({previewToken}) {
    return (
      <BrowserPreview
        url={`http://localhost:3000/api/preview?${previewToken}`}
      />
    )
  }
})

variants.code:
import {registerPreview} from 'alinea/preview'
registerPreview({
  refetch() {
    // Reload server data
  }
})

// A react hook is available
import {usePreview} from 'alinea/preview/react'
// Use this hook once to listen and react to preview changes
const {isPreviewing} = usePreview({
  refetch() {
    // Reload server data & redraw
  }
})

// A hook specifically for next.js
import {useNextPreview} from 'alinea/preview/next'
// Use this hook once to automatically reload static/server props
const {isPreviewing} = useNextPreview()
